import { Component, Input } from '@angular/core';
import { FormGroup, FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ModalOptions, InstanceOptions, Modal } from 'flowbite';
import { NgxSpinnerService } from 'ngx-spinner';
import { Status } from 'src/app/models/policy-status';
import { ApiService } from 'src/app/shared/services';

@Component({
  selector: 'app-update-payment-settings',
  templateUrl: './update-payment-settings.component.html',
  styleUrls: ['./update-payment-settings.component.css']
})
export class UpdatePaymentSettingsComponent {  
paymentSettingsForm!: FormGroup;
statusOptions = Object.values(Status);
data: any
paymentSettings:any;
paymentSettingsData:any
@Input() paymentSettingsId!: string;

constructor(private spinner: NgxSpinnerService, private route: ActivatedRoute,
  private router: Router, private fb: FormBuilder, private service: ApiService) {
} 


ngOnInit(){
  this.route.params.subscribe((params : any) => {
    const paymentSettingsId = +params['id'];
    this.paymentSettings = this.fetchpaymentSettings(paymentSettingsId);

  });

  this.paymentSettingsForm = this.fb.group({
    name: '',
    description: '',
    status: ''
  });
  }



fetchpaymentSettings(paymentSettingsId:number) {
  if (paymentSettingsId) {
    this.service.getFromUrl(`payment-settings/${paymentSettingsId}`).subscribe(x => this.paymentSettingsForm.patchValue(x))
  }

}

onSubmit(event: Event) {
  event.preventDefault(); 
  if (this.paymentSettingsForm.valid) {
    this.spinner.show() 
    this.service.updateToUrl(`payment-settings/${this.paymentSettingsId}`, this.paymentSettingsForm.value).subscribe((res) => {
      this.data = res;
      this.spinner.hide()
      this.router.navigate(['/payment-settings']);
  });
  }
}


}
<div>
  <div class="relative bg-white rounded-lg shadow dark:bg-gray-700">
      <div class="flex items-center justify-between p-4 md:p-5 border-b rounded-t dark:border-gray-600">
          <h3 class="text-lg font-semibold text-gray-900 dark:text-white">
              Update Payment Settings
            </h3>

        </div>
        <form class="p-4 md:p-5" [formGroup]="paymentSettingsForm" (submit)="onSubmit($event)">
          <div class="grid gap-6 mb-6 md:grid-cols-2">
              <div class="col-span-2">
                  <label for="name" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Currency</label>
                  <input type="text" formControlName="currency" name="name" id="name" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white dark:focus:ring-primary-500 dark:focus:border-primary-500" placeholder="Type currency" required="">
              </div>

              <div class="col-span-2">
                <label for="status" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Select a status</label>
                <select formControlName="status" id="status" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
                  <option *ngFor="let status of statusOptions" [value]="status">{{status}}</option>
                </select>
              </div>

              <div class="col-span-2">
                  <label for="rate" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Rate</label>
                  <input type="number" formControlName="rate" name="rate" id="rate" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white dark:focus:ring-primary-500 dark:focus:border-primary-500" placeholder="Type rate" required="">
              </div>

          </div>
          <button type="submit" class="text-white inline-flex items-center bg-purple-700 hover:bg-purple-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
              <svg class="me-1 -ms-1 w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clip-rule="evenodd"></path></svg>
             Update
          </button>
      </form>
</div>
</div>
