import { Component, Input } from '@angular/core';
import { FormGroup, FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ModalOptions, InstanceOptions, Modal } from 'flowbite';
import { NgxSpinnerService } from 'ngx-spinner';
import { Status } from 'src/app/models/policy-status';
import { ApiService } from 'src/app/shared/services';
import { first } from 'rxjs/operators';

@Component({
  selector: 'app-update-premiums',
  templateUrl: './update-premiums.component.html',
  styleUrls: ['./update-premiums.component.css']
})
export class UpdatePremiumsComponent {
  premiumsForm!: FormGroup;
  statusOptions = Object.values(Status);
  data: any
  @Input() premiumId!: string;
  planOptions: any[] = [];
  premium:any

  constructor(private spinner: NgxSpinnerService, private route: ActivatedRoute,
    private router: Router, private fb: FormBuilder, private service: ApiService) {
  } 

  ngOnInit() {
    this.route.params.subscribe((params : any) => {
      const premiumId = params['id'];
      this.premiumId = premiumId
      this.premium = this.getPremium(premiumId);
    });

    this.premiumsForm = this.fb.group({
      name: '',
      description: '',
      status: '',
      premiumId:'',
      amount:''
    });

    this.service.getAll('plan').subscribe((data) => {
      this.planOptions = data.content;
    });
  }

getPremium(premiumId:any){
  this.service.getFromUrl(`premiums/${premiumId}`).pipe(first())
  .subscribe(x => this.premiumsForm.patchValue(x))

}
  onSubmit(event: Event) {
    event.preventDefault(); 
    if (this.premiumsForm.valid) { 
      this.spinner.show()
      this.service.updateToUrl(`premiums/${this.premiumId}`, this.premiumsForm.value).subscribe((res) => {
        this.data = res;
        this.spinner.hide()
        this.router.navigate(['/premiums']);

      });
    }
  }


}
<div class="flex justify-between items-center">
    <p class=" text-xl text-gray-900 dark:text-white font-bold">
      Update Pre
    </p>
    <nav class="flex  py-1 text-gray-700" aria-label="Breadcrumb">
      <ol class="inline-flex items-center space-x-1 md:space-x-2 rtl:space-x-reverse">
        <li aria-current="page">
          <div class="flex items-center">
            <a href="/payment-settings"><span class="ms-1 text-sm font-small text-gray-500 md:ms-2 dark:text-gray-400">Previous Page</span>
            </a><svg class="rtl:rotate-180  w-3 h-3 mx-1 text-gray-400" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 6 10">
              <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 9 4-4-4-4"/>
            </svg>

          </div>
        </li>
      </ol>
    </nav>
  </div>
  <div>    
   <div class="mt-5 relative bg-white rounded-lg shadow dark:bg-gray-700">
          <form class="p-4 md:p-5" [formGroup]="premiumsForm" (submit)="onSubmit($event)">
            <div class="grid gap-6 mb-6 md:grid-cols-2">
                <div class="col-span-2">
                    <label for="status" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Select a Plan</label>
                    <select formControlName="planId" id="status" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
                        <option *ngFor="let plan of planOptions" [value]="plan.id3">{{plan.name}}</option>
                      </select>
                  </div>

                <div class="col-span-2">
                    <label for="name" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Name</label>
                    <input type="text" formControlName="name" name="name" id="name" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white dark:focus:ring-primary-500 dark:focus:border-primary-500" placeholder="Type premiums name" required="">
                </div>

                <div class="col-span-2">
                    <label for="name" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Amount</label>
                    <input type="number" formControlName="amount" name="name" id="name" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white dark:focus:ring-primary-500 dark:focus:border-primary-500" placeholder="$400" required="">
                </div>

                <div class="col-span-2">
                    <label for="status" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Select status</label>
                    <select formControlName="status" id="status" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500">
                      <option *ngFor="let status of statusOptions" [value]="status">{{status}}</option>
                    </select>
                  </div>

                <div class="col-span-2">
                    <label for="description" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Description</label>
                    <textarea id="description" formControlName="description" rows="4" class="block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Write description here"></textarea>                    
                </div>

            </div>
            <button type="submit" class="text-white inline-flex items-center bg-purple-700 hover:bg-purple-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
                <svg class="me-1 -ms-1 w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clip-rule="evenodd"></path></svg>
               Update
            </button>
        </form>
  </div>
  </div>
